mapper.select:
  
    select   p.serial_number as "serialNumber",p.`name` as "projectName" , w.id as 'id',
             p.type as "projectType",p.product_manager as "userName",
             w.progress as "projectStatus",w.end_date as "endDate"  ,w.start_date as "startDate"
         from   project p,weekly  w 
        
             p.id=w.project_id
          
            
	           and   p.`name` = #{projectName,jdbcType=VARCHAR}
	        
	        
	         
	            and   p.type = #{projectType,jdbcType=VARCHAR}
	        
		
		    
	            and  p.project_manager= #{userName,jdbcType=VARCHAR}
	        
	       
	        
	           and   p.project_state= #{projectStatus,jdbcType=INTEGER}
	        
	        
		    
	           and   p.serial_number = #{serialNumber,jdbcType=VARCHAR}
	        
	        
			　　AND w.start_date >= 	STR_TO_DATE(#{startDate},'YYYY-MM-DD HH24:mi:ss')
			 
			
			  AND  w.end_date   <=	STR_TO_DATE(#{endDate},'YYYY-MM-DD HH24:mi:ss')

select p.project_manager as "userName", p.serial_number as "serialNumber", p.`name` as "projectName", p.type as "projectType" ,a.business_owner as "businessParty",a.expected_launch_date as "planDate", a.dep as "projectInDept",p.id as "projectId" from project p ,project_apply a,weekly w
a.ID=p.apply_id and w.project_id=p.id
and w.id=#{_parameter,jdbcType=BIGINT}

select v.version from project_version v WHERE v.id in ( select DISTINCT t.version_id from task t
t.start_date >= subdate(curdate(),date_format(curdate(),'%w')-1) and t.end_date < subdate(curdate(),date_format(curdate(),'%w')-6) and t.start_date < t.end_date
and t.project_id=#{_parameter,jdbcType=BIGINT}
)

select v.name from project_phase v WHERE v.id in ( select DISTINCT t.phase_id from task t
t.start_date >= subdate(curdate(),date_format(curdate(),'%w')-1) and t.end_date < subdate(curdate(),date_format(curdate(),'%w')-6) and t.start_date < t.end_date
and t.project_id=#{_parameter,jdbcType=BIGINT}
)

select v.name from project_phase v WHERE v.id in ( select DISTINCT t.phase_id from task t
t.start_date >= subdate(curdate(),date_format(curdate(),'%w')-8) and t.end_date < subdate(curdate(),date_format(curdate(),'%w')-13) and t.start_date < t.end_date
and t.project_id=#{_parameter,jdbcType=BIGINT}
)

select w.question from weekly w
w.start_date >= subdate(curdate(),date_format(curdate(),'%w')-1) and w.end_date < subdate(curdate(),date_format(curdate(),'%w')-6) and w.start_date < w.end_date
and w.id = #{_parameter,jdbcType=VARCHAR}
