mapper.select:
         select  w.id  as 'id',w.project_id as 'projectId'
          
		   from  weekly  w
		   
		 
		        1=1
		     
			    and  w.project_id=#{projectId,jdbcType=BIGINT}
			
			 
	           and w.start_date >=#{startDate,jdbcType=VARCHAR}
			 
			
			   and  w.end_date   <=#{endDate,jdbcType=VARCHAR}

  
    select   p.serial_number as "serialNumber",p.`name` as "projectName" , w.id as 'id',p.apply_id as "applyId",
             p.type as "projectType",p.project_manager as "userName",p.id as "projectId",w.created as 'created',
             p.completed_progress as "projectStatus",w.end_date as "endDate"  ,w.start_date as "startDate"
         from   project p,weekly  w
        
             p.id=w.project_id
          
            
            and   p.`name` = #{weeklyPara.projectName,jdbcType=VARCHAR}
	        
	        
	         
	         and   p.type = #{weeklyPara.projectType,jdbcType=VARCHAR}
	        
		
		    
		    and  p.project_manager= #{weeklyPara.userName,jdbcType=VARCHAR}
	        
	       
	        
	        and   p.project_state= #{weeklyPara.projectStatus,jdbcType=INTEGER}
	        
	        
		    
		    and   p.serial_number = #{weeklyPara.serialNumber,jdbcType=VARCHAR}
	        
	        
	        and w.start_date >=#{weeklyPara.startDate,jdbcType=VARCHAR}
			 
			
			and  w.end_date   <=#{weeklyPara.endDate,jdbcType=VARCHAR}
			
			
			
		    	limit ${(page.pageIndex-1)*page.pageSize},${page.pageSize}

  
       select count(1)  as 'total' from (
       
           select   p.serial_number as "serialNumber",p.`name` as "projectName" , w.id as 'id',p.apply_id as "applyId",
             p.type as "projectType",p.project_manager as "userName",
              p.completed_progress as "projectStatus",w.end_date as "endDate"  ,w.start_date as "startDate"
         from   project p,weekly  w
        
             p.id=w.project_id
          
            
            and   p.`name` = #{projectName,jdbcType=VARCHAR}
	        
	        
	         
	         and   p.type = #{projectType,jdbcType=VARCHAR}
	        
		
		    
		    and  p.project_manager= #{userName,jdbcType=VARCHAR}
	        
	       
	        
	        and   p.project_state= #{projectStatus,jdbcType=INTEGER}
	        
	        
		    
		    and   p.serial_number = #{serialNumber,jdbcType=VARCHAR}
	        
	        
	        and w.start_date >=#{startDate,jdbcType=VARCHAR}
			 
			
			and  w.end_date   <=#{endDate,jdbcType=VARCHAR}
			
			
		
		    
	      
		    
   
    
       )   as temp

    
     select  v.version   from  project_version v
          WHERE v.id in 
             
               #{item}

  
          select  v.name   from  project_phase v 
          WHERE v.id in 
         ( select     DISTINCT t.phase_id   from  task t 
		   
		           t.start_date >=#{startDate,jdbcType=VARCHAR}
		          and  t.end_date <= #{endDate,jdbcType=VARCHAR}
		          and  t.start_date <= t.end_date 
		          
		          
		      
			    and  t.project_id=#{id,jdbcType=BIGINT}
			  
		   
		    
         )


        select  v.name   from  project_phase v 
        WHERE v.id in 
         
             #{item}

select t.`name` ,t.phase_id ,t.plan_time ,t.id , t.`owner`, t.version_id , t.status ,t.start_date , t.end_date, d.over_hour ,d.task_hour from task t LEFT JOIN task_details d on t.id=d.task_id where 1=1 and t.end_date >= #{startDate,jdbcType=VARCHAR} and t.end_date <= #{endDate,jdbcType=VARCHAR} and t.start_date <= t.end_date
and t.project_id=#{id,jdbcType=BIGINT}
UNION
select t.`name` ,t.phase_id ,t.plan_time ,t.id , t.`owner`, t.version_id , t.status ,t.start_date , t.end_date, d.over_hour ,d.task_hour from task t LEFT JOIN task_details d on t.id=d.task_id
t.end_date >=#{endDate,jdbcType=VARCHAR} and t.start_date <= t.end_date
and t.project_id=#{id,jdbcType=BIGINT}

       
      
		      
		      
		       select   t.`name`  ,t.plan_time ,t.phase_id as 'phaseId',
		       
		         t.`owner`, t.describe  ,t.end_date
		
		        from  task  t
		        
		               t.end_date <= #{endDate,jdbcType=VARCHAR}
		                and  t.start_date <= t.end_date 
		         
			           and  t.project_id=#{id,jdbcType=BIGINT}
			     	      
			     
		       UNION
		       select   t.`name`  ,t.plan_time ,t.phase_id as 'phaseId',
		       
		         t.`owner`, t.describe  ,t.end_date
		
		        from  task  t
		         
		             t.end_date >=#{endDate,jdbcType=VARCHAR}
		                and  t.start_date <= t.end_date 
		         
			           and  t.project_id=#{id,jdbcType=BIGINT}

       
       select   w.risk
		
		     from    weekly  w
		      
		      
		        w.start_date >=#{startDate,jdbcType=VARCHAR}
		          and  w.end_date <=#{endDate,jdbcType=VARCHAR}
		          and  w.start_date <= w.end_date 
			    
		         and      w.id = #{id,jdbcType=VARCHAR}

       
       select   
		    w.question  
		         from  weekly  w
		        
		         
		          w.start_date >=#{startDate,jdbcType=VARCHAR}
		          and  w.end_date <=#{endDate,jdbcType=VARCHAR}
