mapper.select:
  
    select   p.serial_number as "serialNumber",p.`name` as "projectName" , w.id as 'id',
             p.type as "projectType",p.product_manager as "userName",
             w.progress as "projectStatus",w.end_date as "endDate"  ,w.start_date as "startDate"
         from   project p,weekly  w 
        
             p.id=w.project_id
          
            
	           and   p.`name` = #{projectName,jdbcType=VARCHAR}
	        
	        
	         
	            and   p.type = #{projectType,jdbcType=VARCHAR}
	        
		
		    
	            and  p.project_manager= #{userName,jdbcType=VARCHAR}
	        
	       
	        
	           and   p.project_state= #{projectStatus,jdbcType=INTEGER}
	        
	        
		    
	           and   p.serial_number = #{serialNumber,jdbcType=VARCHAR}
	        
	        
			　　AND w.start_date >= 	STR_TO_DATE(#{startDate},'YYYY-MM-DD HH24:mi:ss')
			 
			
			  AND  w.end_date   <=	STR_TO_DATE(#{endDate},'YYYY-MM-DD HH24:mi:ss')
